  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'requirements',
	title: 'Requirements',
	sidebar_label: 'Requirements'
};
export const metadata = {
  "unversionedId": "chatbot/requirements",
  "id": "chatbot/requirements",
  "isDocsHomePage": false,
  "title": "Requirements",
  "description": "REQUIREMENTS",
  "source": "@site/docs/chatbot/requirements.md",
  "permalink": "/docs/chatbot/requirements",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/chatbot/requirements.md",
  "sidebar_label": "Requirements",
  "sidebar": "chatbot",
  "previous": {
    "title": "Introduction",
    "permalink": "/docs/chatbot/Introduction"
  }
};
  /* @jsx mdx */

export const rightToc = [];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "requirements"
    }}>{`REQUIREMENTS`}</h1>
    <p>{`The Chatbot requires:`}</p>
    <ul>
      <li parentName="ul">{`atleast 4GB RAM `}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.python.org/downloads/release/python-370/"
        }}>{`Python 3.7`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/google-research/bert"
        }}>{`Pre-trained BERT`}</a>{` : Any of the models can be downloaded but `}<a parentName="li" {...{
          "href": "https://storage.googleapis.com/bert_models/2018_10_18/cased_L-12_H-768_A-12.zip"
        }}>{`BERT-base, Cased`}</a>{` is recommended`}</li>
    </ul>
    <h1 {...{
      "id": "installation"
    }}>{`INSTALLATION`}</h1>
    <ul>
      <li parentName="ul">{`Download the code for the Chatbot from the repository`}</li>
      <li parentName="ul">{`Unzip the downloaded BERT model and place it in the main directory of the chatbot. `}{`[Note : Make sure the name of the BERT model folder and the value of model_dir in run_bert_service.sh are SAME ]`}</li>
      <li parentName="ul">{`Navigate to the chatbot directory and run the following command in the terminal:`}<pre parentName="li"><code parentName="pre" {...{
            "className": "language-sh"
          }}>{`$ pip install -r requirements.txt
$ pip install rasa-x==0.20.1 --extra-index-url https://pypi.rasa.com/simple
`}</code></pre></li>
      <li parentName="ul">{`After these commands finish running, run the following command in the terminal:`}<pre parentName="li"><code parentName="pre" {...{
            "className": "language-sh"
          }}>{`$ ./run_bert_service.sh
`}</code></pre></li>
      <li parentName="ul">{`After the BERT has started and is ready to serve requests , open another terminal window and run:`}<pre parentName="li"><code parentName="pre" {...{
            "className": "language-sh"
          }}>{`$ rasa run actions
`}</code></pre></li>
      <li parentName="ul">{`After the action server has started and is listening on port 5000, open another terminal window to start rasa x as:`}<pre parentName="li"><code parentName="pre" {...{
            "className": "language-sh"
          }}>{`$ rasa x
`}</code></pre></li>
      <li parentName="ul">{`Once Rasa X has started, you can see the Rasa X UI running on the localhost, you can talk to your bot in that window or `}<a parentName="li" {...{
          "href": "https://ngrok.com/"
        }}>{`ngrok`}</a>{` the Rasa X and share it with other testers to improve it.`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
